import {useState, useEffect} from 'react'

const useForm = () => {
    const[values, setValues] = useState({
        session: '',
        speaker: '',
        timeslot: '',
        room: ''
    })
    const[errors, setErrors] = uesState({})


    const handelChange = e => {
        const {name, value} = e.target
        setValues({
            ...values,
            [name]: value
        }
        )
    }

    return {handelChange}
}

export default useForm;
