import React from 'react';
import './Session.css';
import Form from './Form';

class Session extends React.Component {
  constructor(props) {
     super(props)
     this.state = {
        content: [
           { session: 1, timeslot: 'Wasif', speaker: 21, room: 'wasif@email.com', action: ' '},
           { session: 2, timeslot: 'Ali', speaker: 19, room: 'ali@email.com', action: ' ' },
           { session: 3, timeslot: 'Saad', speaker: 16, room: 'saad@email.com', action: ' ' },
           { session: 4, timeslot: 'Asad', speaker: 25, room: 'asad@email.com', action: ' ' }
        ]
     }
  }

  handleFormSubmit = (e) => {
    e.preventDefault();

    let items = [...this.state.content];

    content.push({
      session: this.state.username,
      timeslot: this.state.password
    });

    this.setState({
      items,
      username: '',
      password: ''
    });
  };

  renderTableHeader() {
     let header = Object.keys(this.state.content[0])
     return header.map((key, index) => {
        return <th key={index}>{key.toUpperCase()}</th>
     })
  }

  renderTableData() {
     return this.state.content.map((header, index) => {
        const { session, timeslot, speaker, room, action} = header //destructuring
        return (
           <tr key={session}>
              <td>{session}</td>
              <td>{timeslot}</td>
              <td>{speaker}</td>
              <td>{room}</td>
              <td>{action}</td>
           </tr>
        )
     })
  }

  render() {
     return (
        <div>
          <Form>
          </Form>
           <h1 id='title'>Session Table</h1>
           <table id='header'>
              <tbody>
                 <tr>{this.renderTableHeader()}</tr>
                 {this.renderTableData()}
              </tbody>
           </table>
        </div>
     )
  }
}

function Session1() {
    return (
        <div className='products'>
      <h1>Session</h1>
    </div>
    )
}
export default Session;
