import React from 'react';
import './Session.css';
import Form from './Form';

class Session extends React.Component {
  constructor(props) {
     super(props)
     this.state = {
        session: '',
        timeslot: '',
        speaker: '',
        room: '',
        action: '',
        content: [
           { session: 1, timeslot: 'Wasif', speaker: 21, room: 'wasif@email.com', action: ' '},
           { session: 2, timeslot: 'Ali', speaker: 19, room: 'ali@email.com', action: ' ' },
           { session: 3, timeslot: 'Saad', speaker: 16, room: 'saad@email.com', action: ' ' },
           { session: 4, timeslot: 'Asad', speaker: 25, room: 'asad@email.com', action: ' ' }
        ]
     }
  }

  handleFormSubmit = (e) => {
    e.preventDefault();

    let content = [...this.state.content];

    content.push({
      session: this.state.session,
      timeslot: this.state.timeslot,
      speaker: this.state.speaker,
      room: this.state.room,
      action: this.state.action
    });

    this.setState({
      content,
      session: '',
      timeslot: '',
      speaker: '',
      room: '',
      action: ''
    });
  };

  handleInputChange = (e) => {
    let input = e.target;
    let name = e.target.name;
    let value = input.value;

    this.setState({
      [name]: value
    })
  }

  renderTableHeader() {
     let header = Object.keys(this.state.content[0])
     return header.map((key, index) => {
        return <th key={index}>{key.toUpperCase()}</th>
     })
  }

  renderTableData() {
      const content = this.props.content;
      return this.state.content.map((header, index) => {
        const { session, timeslot, speaker, room, action} = header //destructuring
        return (
           <tr key={content.session}>
              <td>{content.session}</td>
              <td>{content.timeslot}</td>
              <td>{content.speaker}</td>
              <td>{content.room}</td>
              <td>{content.action}</td>
           </tr>
        )
     })
  }

  render() {
     return (
        <div>
          <Form handleFormSubmit={ this.handleFormSubmit } handleInputChange={ this.handleInputChange }
          newSession={ this.state.session }
          newTimeslot={ this.state.timeslot }
          newSpeaker={ this.state.speaker }
          newRoom={ this.state.room }
          newAction={ this.state.action }>
          </Form>
           <h1 id='title'>Session Table</h1>
           <table id='header'>
              <tbody>
                 <tr>{this.renderTableHeader()}</tr>
                 {this.renderTableData()}
              </tbody>
           </table>
        </div>
     )
  }
}

function Session1() {
    return (
        <div className='products'>
      <h1>Session</h1>
    </div>
    )
}
export default Session;
