import React from 'react';
import './Session.css';
import Table from './Table';
import Form from './Form';

class Session extends React.Component {
  constructor() {
    super();

    this.state = {
      session: '',
      timeslot: '',
      speaker: '',
      room: '',
      action: '',
      items: []
    }
  };

  handleFormSubmit = (e) => {
    e.preventDefault();

    let items = [...this.state.items];

    items.push({username: this.state.username, password: this.state.password});

    this.setState({
      items,
      username: '',
      password: ''
    });
  };

  handleInputChange = (e) => {
    let input = e.target;
    let name = e.target.name;
    let value = input.value;

    this.setState({
      [name]: value
    })
  };

  render() {
    return (
      <div className="App">
        <Form handleFormSubmit={ this.handleFormSubmit } handleInputChange={ this.handleInputChange } newUsername={ this.state.username } newPassword={ this.state.password }/>
        <Table items={ this.state.items }/>
      </div>
    );
  }
}

function Session1() {
    return (
        <div className='products'>
      <h1>Session</h1>
    </div>
    )
}
export default Session;
